mapper.insert:
 insert into board
 (
    board_title
  , board_content
  , board_writer
  , board_view_cnt
  , category
  , board_status
  , reg_dt
  , upt_dt
 )
values
(
   #{boardTitle}
 , #{boardContent}
 , #{boardWriter}
 , 0
 , #{category}
 , #{boardStatus}
 , localtimestamp
 , localtimestamp
)

insert into board_img
(
   board_id
 , emoticon_img_nm
 , img_ext_nm
 , img_file_size
 , file_url_path
)
values
(
   #{boardId}
 , #{emoticonImgNm}
 , #{imgExtNm}
 , #{imgFileSize}
 , #{uploadFilePath}
)

mapper.update:
update board
   set board_title = #{boardTitle}
     , board_content = #{boardContent}
     , board_status = #{boardStatus}
     , upt_dt = localtimestamp
  where board_id = #{boardId}
