mapper.select:
select common_code , common_code_name , common_code_description , ref , depth , use_yn , reg_dt , reg_id , upt_dt , upt_id from common_code a
where a.ref = #{commonCode}
where a.depth = 1

mapper.insert:
insert into common_code
        ( common_code
        , common_code_name
        , common_code_description
        , depth
        , use_yn
        , reg_dt
        , reg_id
        , upt_dt
        , upt_id)
values ( #{commonCode}
       , #{commonCodeName}
       , #{commonCodeDescription}
       , #{depth}
       , #{useYn}
       , now()
       , #{regId}
       , now()
       , #{uptId});

mapper.update:
update common_code
set common_code             = #{updateCommonCode}
  , common_code_name        = #{commonCodeName}
  , common_code_description = #{commonCodeDescription}
  , use_yn                  = #{useYn}
  , upt_dt                  = now()
  , upt_id                  = #{uptId}
where common_code = #{commonCode}
